import React, { useState } from "react";
import "./App.css";

const App = () => {
  const plata = [
    {
      moneda: "euro",
      cambio: 1,
    },
    {
      moneda: "peso colombiano",
      cambio: 5.25,
    },
    {
      moneda: "peso argentino",
      cambio: 206,
    },
    {
      moneda: "peso mexicano",
      cambio: 19,
    },
  ];

  const [cambiar, setCambiar] = useState();
  return (
    <>
      <div className="box">
        <label className="label" htmlFor="a">
          {plata[cambiar(0)]}
        </label>
        <input id="a"></input>

        <label className="label" htmlFor="a">
          Peso colombiano
        </label>

        <input className="" id="a" type="number"></input>
        <label className="label" htmlFor="a">
          Peso argentino
        </label>
        <input className="" id="a" type="number"></input>

        <label className="label" htmlFor="a">
          Peso mexicano
        </label>

        <input className="" id="a" type="number"></input>
      </div>
    </>
  );
};

export default App;
